mapper.select:
SELECT a.*, b.prodName, b.prodCate1, b.prodCate2 FROM `km_product_review` as a
join `km_product` as b
on a.prodNo = b.prodNo
where a.uid = #{uid}
ORDER BY `revNo` DESC LIMIT #{start}, 5;

SELECT a.ordDate, c.company, c.prodName, b.`count`, a.ordNo, b.total, a.ordComplete, c.prodCate1, c.prodCate2, c.thumb1, c.uid FROM km_product_order AS a JOIN km_product_order_item AS b ON a.ordNo =b.ordNo JOIN km_product AS c ON c.prodNo = b.prodNo WHERE a.ordUid = #{uid}
AND DATE_FORMAT(a.ordDate, '%Y-%m-%d') = #{date}
ORDER BY a.ordDate DESC LIMIT #{start}, 10;

SELECT
    *
FROM km_member_point AS a
WHERE a.uid = #{uid}
ORDER BY a.pointDate DESC
LIMIT 5;
